import React, { useState } from "react";
import "./booking.css";
import { Form, FormGroup, Button } from "reactstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Booking = ({ tour, avgRating, onBookingSubmit }) => {
  const { price } = tour;
  const navigate = useNavigate();

  const [credentials, setCredentials] = useState({
    userId: "01",
    userEmail: "user@gmail0com",
    fullname: "",
    phone: "",
    cin: "",
    class:"",
    aller: "",
    arriver: "",
    datedepart: "", // Nouveau champ de destination
    dateretour: "", // Nouveau champ de destination
  });

  const handelChange = (e) => {
    setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const handleClick = async (e) => {
    e.preventDefault();
    console.log(credentials);

    try {
      // Sauvegarder dans destination
      await axios.post("http://localhost:3006/destination", {
        aller: credentials.aller,
        arriver: credentials.arriver,
       
      });

      // Sauvegarder dans vols
      await axios.post("http://localhost:3006/vols", {
        volNumber: "vol_number", // Remplacez par le numéro de vol réel
        datedepart: credentials.datedepart,
        dateretour: credentials.dateretour,
        classe: "classe", // Remplacez par la classe réelle
        aller:credentials.aller,
        arriver:credentials.arriver
      });

      // Passer les données des informations au composant parent
      onBookingSubmit(credentials);

      navigate("/thank-you");
    } catch (error) {
      console.error("Erreur lors de l'enregistrement des données :", error);
    }
  };

  return (
    <div className="booking-container">
      <div className="booking__top d-flex align-items-center justify-content-between">
        <h3>
          $ {price} <span>/ par personne</span>
        </h3>
        <span className="tour__rating d-flex align-items-center gap-1">
          <i
            className="ri-star-s-fill"
            style={{ color: "var(--secondary-color)" }}
          ></i>
          {avgRating === 0 ? null : avgRating}
        </span>
      </div>
      <div className="booking__form">
        <h5>Informations</h5>
        <Form className="booking__info-form" onSubmit={handleClick}>
          <FormGroup>
            <input
              type="text"
              placeholder="Nom complet"
              id="fullname"
              required
              onChange={handelChange}
            />
          </FormGroup>
          <FormGroup>
            <input
              type="number"
              placeholder="Téléphone"
              id="phone"
              required
              onChange={handelChange}
            />
          </FormGroup>
          <FormGroup>
            <input
              type="text"
              placeholder="CIN"
              id="cin"
              required
              onChange={handelChange}
            />
          </FormGroup>
          <FormGroup>
            <input
              type="text"
              placeholder="Classe"
              id="classe"
              required
              onChange={handelChange}
            />
          </FormGroup>
          <FormGroup>
            <input
              type="text"
              placeholder="Deaprt "
              id="aller"
              required
              onChange={handelChange}
            />
          </FormGroup>
          <FormGroup>
            <input
              type="text"
              placeholder=" Arrivee"
              id="arriver"
              required
              onChange={handelChange}
            />
          </FormGroup>
          <FormGroup className="d-flex align-items-center gap-3">
            De :{" "}
            <input
              type="date"
              placeholder=""
              id="datedepart"
              required
              onChange={handelChange}
            />
            <br />
            À :{" "}
            <input
              type="date"
              placeholder=""
              id="dateretour"
              required
              onChange={handelChange}
            />
          </FormGroup>
          <Button className="btn primary__btn" type="submit">
Book Now          </Button>
        </Form>
      </div>
    </div>
  );
};

export default Booking;
